import { Injectable } from '@angular/core';
import { Product } from './product';
import { PRODUCTS } from './mock-products';
import { Observable, of } from 'rxjs';
import { MessageService } from './message.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { catchError, map, tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ProductService {

  private productsUrl = "http://127.0.0.1:5000/shipping/products"

  constructor(private http: HttpClient, private messageService:MessageService) { }

  private log(message: string) {
    this.messageService.add(`HeroService: ${message}`);
  }

  getProducts(): Observable<Product[]> {
    // const products = of(PRODUCTS)
    this.messageService.add('ProductService: fetched products');
    return this.http.get<Product[]>(this.productsUrl)
    // return products
  }

  getProduct(id: number): Observable<Product> {
    // For now, assume that a product with the specified `id` always exists.
    // Error handling will be added in the next step of the tutorial.
    const product = PRODUCTS.find(h => h.id === id)!;
    this.log(`ProductService: fetched product id=${id}`)
    // this.messageService.add(`ProductService: fetched product id=${id}`);
    return of(product);
  }

}
